mapper.select:
select t.id,u1.name as owner, t.money, t.name, t.expected_date,cu.name as customer_id,dv1.value as stage,dv2.value as type,dv3.value as source,a.name as activity_id, co.fullname as contacts_id,u2.name as create_by, t.create_time,u3.name as edit_by, t.edit_time, t.description, t.contact_summary, t.next_contact_time from tbl_tran t join tbl_user u1 on t.owner=u1.id left join tbl_customer cu on t.customer_id=cu.id left join tbl_dic_value dv1 on t.stage=dv1.id left join tbl_dic_value dv2 on t.type=dv2.id left join tbl_dic_value dv3 on t.source=dv3.id left join tbl_activity a on t.activity_id=a.id join tbl_contacts co on t.contacts_id=co.id join tbl_user u2 on t.create_by=u2.id left join tbl_user u3 on t.edit_by=u3.id
and u1.name like '%' #{owner} '%'
and t.name like '%' #{name} '%'
and cu.name like '%' #{customerName} '%'
and dv1.value=#{stage}
and dv2.value=#{transactionType}
and dv3.value=#{source}
and co.fullname like '%' #{contactsName} '%'
order by t.create_time desc limit #{beginNo},#{pageSize}

select count(*) from tbl_tran t join tbl_user u1 on t.owner=u1.id left join tbl_customer cu on t.customer_id=cu.id left join tbl_dic_value dv1 on t.stage=dv1.id left join tbl_dic_value dv2 on t.type=dv2.id left join tbl_dic_value dv3 on t.source=dv3.id left join tbl_activity a on t.activity_id=a.id join tbl_contacts co on t.contacts_id=co.id join tbl_user u2 on t.create_by=u2.id left join tbl_user u3 on t.edit_by=u3.id
and u1.name like '%' #{owner} '%'
and t.name like '%' #{name} '%'
and cu.name like '%' #{customerName} '%'
and dv1.value=#{stage}
and dv2.value=#{transactionType}
and dv3.value=#{source}
and co.fullname like '%' #{contactsName} '%'
